mapper.delete:
delete from course_selection
where cid = #{cid,jdbcType=VARCHAR}
  and sid = #{sid,jdbcType=VARCHAR}

mapper.update:
update course_selection
set score = #{score,jdbcType=TINYINT}
where cid = #{cid,jdbcType=VARCHAR}
  and sid = #{sid,jdbcType=VARCHAR}

mapper.select:
select cid, sid, score
from course_selection
where cid = #{cid,jdbcType=VARCHAR}
  and sid = #{sid,jdbcType=VARCHAR}
